<?php\n// Set a cookie using a string variable as the name\n$cookieName = "user_preference";\n$cookieValue = "dark_mode";\nsetcookie($cookieName, $cookieValue, time() + (86400 * 30), "/"); // 86400 = 1 day\n\n// Retrieve the cookie value using the same variable\nif(isset($_COOKIE[$cookieName])) {\n    echo "Cookie value is: " . $_COOKIE[$cookieName];\n} else {\n    echo "Cookie is not set.";\n}\n?>