<?php\nif ($_SERVER['REQUEST_METHOD'] == 'POST') {\n    $name = $_POST['name'];\n    $job = $_POST['job'];\n    \n    // Now you can use $name and $job in your PHP code\n    // For example, you can assign to a variable\n    $buffer_data = [\n        'name' => $name,\n        'job' => $job\n    ];\n\n    // Send a response back to the client if needed\n    echo json_encode([\n        'status' => 'success',\n        'data' => $buffer_data\n    ]);\n} else {\n    echo json_encode([\n        'status' => 'error',\n        'message' => 'Invalid request method'\n    ]);\n}\n?>