<?php\n// Path to the video file\n$videoFile = 'path/to/video.mp4';\n// Path to the output audio file\n$outputAudioFile = 'path/to/output/audio.mp3';\n\n// Make sure the video file exists\nif (!file_exists($videoFile)) {\n    die('Video file does not exist');\n}\n\n// Command to extract audio using FFmpeg\n$command = "ffmpeg -i " . escapeshellarg($videoFile) . " -q:a 0 -map a " . escapeshellarg($outputAudioFile) . " 2>&1";\n\n// Execute the command\nexec($command, $output, $return_var);\n\n// Check if the command was successful\nif ($return_var !== 0) {\n    echo "Error extracting audio:\n";\n    echo implode("\n", $output);\n} else {\n    echo "Audio extracted successfully to: " . $outputAudioFile;\n}\n?>