$settings = array(\n    "site_name" => "My Website",\n    "admin_email" => "admin@example.com",\n    "maintenance_mode" => ""\n);\n\n// Retrieve the site name\necho get_setting_value($settings, "site_name", "Default Site Name"); // Output: My Website\n\n// Retrieve the admin email\necho get_setting_value($settings, "admin_email", "default@example.com"); // Output: admin@example.com\n\n// Retrieve the maintenance mode status (which is an empty string)\necho get_setting_value($settings, "maintenance_mode", "off"); // Output: off\n\n// Retrieve a non-existent setting with a default value\necho get_setting_value($settings, "non_existent_setting", "default_value"); // Output: default_value