function getFileCount($path = '.') {\n    $fileCount = 0;\n    $ignore = array('cgi-bin', '.', '..', '.DS_Store');\n    \n    $dh = @opendir($path);\n    if ($dh === false) {\n        return 0;\n    }\n\n    while (false !== ($file = readdir($dh))) {\n        if (!in_array($file, $ignore)) {\n            $fullPath = "$path/$file";\n            if (is_dir($fullPath)) {\n                $fileCount += getFileCount($fullPath); // Recursively count files in subdirectories\n            } elseif (is_file($fullPath)) {\n                $fileCount++;\n            }\n        }\n    }\n\n    closedir($dh);\n    return $fileCount;\n}\n\n// Example usage:\n$directory = './Dir_A';\n$fileCount = getFileCount($directory);\necho "Total files: $fileCount";