<?php\n// File upload logic here\n$uploadedFilePath = 'C:\inetpub\wwwroot\example.com\data\334480\test.jpg';\n\n// Command to grant NETWORK SERVICE full control over the uploaded file\n$cmd = 'icacls ' . escapeshellarg($uploadedFilePath) . ' /grant "NETWORK SERVICE:(OI)(CI)F"';\n\n// Execute the command\nexec($cmd, $output, $returnVar);\n\n// Check if the command was successful\nif ($returnVar !== 0) {\n    // Handle error\n    echo "Failed to set permissions: " . implode("\n", $output);\n} else {\n    echo "Permissions set successfully.";\n}\n?>