<?php\n// Database connection\n$dsn = 'mysql:host=localhost;dbname=testdb';\n$username = 'root';\n$password = '';\n$options = [];\n\ntry {\n    $pdo = new PDO($dsn, $username, $password, $options);\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n\nfunction generateHash($input) {\n    return hash('sha256', $input);\n}\n\n// Insert example\n$item_id = 123;\n$username = 'testuser';\n$hash_value = generateHash($item_id);\n\n$stmt = $pdo->prepare("INSERT INTO Users (username, item_id, item_id_hash) VALUES (?, ?, ?)");\n$stmt->execute([$username, $item_id, $hash_value]);\n\n// Retrieve example\n$user_input_item_id = 123;\n$input_hash = generateHash($user_input_item_id);\n\n$stmt = $pdo->prepare("SELECT * FROM Items WHERE item_id_hash = ?");\n$stmt->execute([$input_hash]);\n$item = $stmt->fetch();\n\nif ($item) {\n    echo "Item Name: " . $item['item_name'];\n} else {\n    echo "Item not found.";\n}\n?>