<?php\n\n// URL of the page to fetch\n$url = "http://www.someaddress.ext/path/";\n\n// Fetch the HTML content of the page\n$htmlContent = file_get_contents($url);\n\nif ($htmlContent === FALSE) {\n    die("Failed to retrieve content from the URL.");\n}\n\n// Parse the HTML content to extract links\n$dom = new DOMDocument();\nlibxml_use_internal_errors(true); // Suppress warnings from invalid HTML\n$dom->loadHTML($htmlContent);\nlibxml_clear_errors();\n\n$links = $dom->getElementsByTagName('a');\n$pk3Links = [];\n\n// Extract .pk3 links\nforeach ($links as $link) {\n    $href = $link->getAttribute('href');\n    if (pathinfo($href, PATHINFO_EXTENSION) === 'pk3') {\n        $pk3Links[] = $href;\n    }\n}\n\n// Directory to save downloaded files\n$downloadDir = 'downloads/';\nif (!is_dir($downloadDir)) {\n    mkdir($downloadDir, 0777, true);\n}\n\n// Download each .pk3 file\nforeach ($pk3Links as $pk3Link) {\n    $fileName = basename($pk3Link);\n    $filePath = $downloadDir . $fileName;\n    \n    $fileContent = file_get_contents($pk3Link);\n    if ($fileContent === FALSE) {\n        echo "Failed to download $pk3Link\n";\n        continue;\n    }\n    \n    file_put_contents($filePath, $fileContent);\n    echo "Downloaded $fileName to $filePath\n";\n}\n\necho "All downloads completed.\n";\n?>