// Get the instance of the front controller\n$frontController = Zend_Controller_Front::getInstance();\n\n// Get the action stack plugin\n$actionStack = $frontController->getPlugin('Zend_Controller_Plugin_ActionStack');\n\n// Retrieve the stack\n$stack = $actionStack->getStack();\n\n// Iterate through the stack to check for a specific action\n$actionToFind = 'userlog'; // Replace with the action name you are looking for\n$found = false;\n\nforeach ($stack as $actionRequest) {\n    if ($actionRequest->getActionName() == $actionToFind) {\n        $found = true;\n        break;\n    }\n}\n\n// Output the result\nif ($found) {\n    echo "Action '$actionToFind' is in the stack.";\n} else {\n    echo "Action '$actionToFind' is not in the stack.";\n}