// Define a route for GET requests to /posts\nRoute::set('posts_index', 'posts')\n    ->defaults(array(\n        'controller' => 'Posts',\n        'action'     => 'index',\n    ))\n    ->filter(function($route, $params, $request) {\n        return $request->method() === HTTP_Request::GET ? $params : false;\n    });\n\n// Define a route for POST requests to /posts\nRoute::set('posts_create', 'posts')\n    ->defaults(array(\n        'controller' => 'Posts',\n        'action'     => 'create',\n    ))\n    ->filter(function($route, $params, $request) {\n        return $request->method() === HTTP_Request::POST ? $params : false;\n    });