<?php\n// Database connection\n$pdo = new PDO('mysql:host=localhost;dbname=calendar', 'username', 'password');\n\n// Insert recurrence rule\n$recurrenceRule = [\n    'frequency' => 'weekly',\n    'interval' => 1,\n    'count' => 10,\n    'until' => '2023-12-31'\n];\n\n$sql = "INSERT INTO recurrence_rules (frequency, interval, count, until) VALUES (:frequency, :interval, :count, :until)";\n$stmt = $pdo->prepare($sql);\n$stmt->execute($recurrenceRule);\n$recurrenceRuleId = $pdo->lastInsertId();\n\n// Insert event\n$event = [\n    'title' => 'Meeting',\n    'start_date' => '2023-01-01',\n    'end_date' => null,\n    'recurrence_rule_id' => $recurrenceRuleId\n];\n\n$sql = "INSERT INTO events (title, start_date, end_date, recurrence_rule_id) VALUES (:title, :start_date, :end_date, :recurrence_rule_id)";\n$stmt = $pdo->prepare($sql);\n$stmt->execute($event);\n?>