<?php\nheader('Content-type: text/xml');\n\n$dbhost = "localhost"; // almost always localhost.\n$dbname = "links"; // Database Name\n$dbuser = "root"; // Database Username\n$dbpass = "password"; // Database Password\n\n$connect = new mysqli($dbhost, $dbuser, $dbpass, $dbname);\n\nif ($connect->connect_error) {\n    die("Connection failed: " . $connect->connect_error);\n}\n\necho "<?xml version='1.0' encoding='UTF-8' ?>\n";\n?>\n<rss version="2.0">\n<channel>\n    <title>Your RSS Feed Title</title>\n    <description>Your RSS Feed Description</description>\n    <link>Your RSS Feed Link</link>\n\n<?php\n$sql = "SELECT * FROM news LIMIT 5";\n$result = $connect->query($sql);\n\nif ($result->num_rows > 0) {\n    while ($row = $result->fetch_assoc()) {\n        ?>\n        <item>\n            <title><?php echo htmlspecialchars($row['title']); ?></title>\n            <author><?php echo htmlspecialchars($row['author']); ?></author>\n            <link>http://MYSITE.com/news.php?id=<?php echo $row['id']; ?></link>\n        </item>\n        <?php\n    }\n} else {\n    echo "<item><title>No news found</title></item>";\n}\n\n$connect->close();\n?>\n</channel>\n</rss>