<?php\n// Step 1: Generate XML content\n$xmlContent = '<?xml version="1.0" encoding="UTF-8"?>';\n$xmlContent .= '<root><element>Sample Data</element></root>';\n\n// Step 2: Compress the XML content using gzip\n$gzippedContent = gzencode($xmlContent);\n\n// Step 3: Set headers to force download\nheader('Content-Type: application/x-gzip');\nheader('Content-Disposition: attachment; filename="data.xml.gz"');\nheader('Content-Length: ' . strlen($gzippedContent));\n\n// Step 4: Output the gzipped content\necho $gzippedContent;\n?>