<?php\n// Determine if it's an audio or video search\n$search_type = isset($_POST['audio_field']) ? 'audio' : 'video';\n$search_field = trim($_POST[$search_type . '_field']);\n$search_engine = trim($_POST[$search_type]);\n\n// Encode the search field\n$url_params = urlencode($search_field);\n\n// Define the URL templates with placeholders for search terms\n$url_templates = array(\n    'audio' => array(\n        'deezer' => 'http://www.deezer.com/s.php?s=',\n        'jiwa' => 'http://www.jiwa.fm/#search/track/%7B%22q%22%3A%22{keywords}%22%7D', // encoded for {"q":"keywords"}\n        'last.fm' => 'http://www.last.fm/music?q='\n    ),\n    'video' => array(\n        'youtube' => 'http://www.youtube.com/results?search_type=&search_query=',\n        'dailymotion' => 'http://www.dailymotion.com/relevance/search/',\n        'google_video' => 'http://video.google.com/videosearch?q='\n    )\n);\n\n// Get the correct URL template based on the search type and engine\n$template = $url_templates[$search_type][$search_engine];\n\n// Replace the placeholder with the actual search terms\n$url = str_replace('{keywords}', $url_params, $template);\n\n// Redirect to the constructed URL\nheader('Location: ' . $url);\nexit;\n?>