$title = $connector->real_escape_string($title);\n$tags = $connector->real_escape_string($tags);\n$category = $connector->real_escape_string($category);\n$blog = $connector->real_escape_string($blog);\n$author = $connector->real_escape_string($author);\n$date = $connector->real_escape_string($date);\n\n$insertQuery = "INSERT INTO blog_articles (title, tags, category, blog, author, date) \n                VALUES ('$title', '$tags', '$category', '$blog', '$author', '$date')";\n\nif ($result = $connector->query($insertQuery)) {\n    // It worked, give confirmation\n    echo '<center><b>Article added to the database</b></center><br>';\n} else {\n    // It hasn't worked so stop and provide error feedback\n    die('Error: ' . $connector->error);\n}