App::import('Core', 'HttpSocket');\nApp::import('Core', 'Xml');\n\n$url = "http://www.site.com/file.xml";\n$httpSocket = new HttpSocket();\n$response = $httpSocket->get($url);\n\nif ($response->isOk()) {\n    $xmlContent = $response->body;\n    $parsedXml = Xml::build($xmlContent);\n    \n    // Now you can work with $parsedXml as needed\n} else {\n    // Handle the error\n    echo "Failed to fetch the XML file.";\n}