// sort.php\n$mysqli = new mysqli("localhost", "username", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$result = $mysqli->query("SELECT * FROM table ORDER BY col DESC");\n\nif ($result->num_rows > 0) {\n    while ($row = $result->fetch_assoc()) {\n        echo "<tbody><tr><td>&#8226;</td><td>" . htmlspecialchars($row["col"]) . "</td><td>" . htmlspecialchars($row["col"]) . "</td><td></td><td></td></tr><tr><td></td><td colspan='4'>" . htmlspecialchars($row["col"]) . "</td></tr></tbody>";\n    }\n} else {\n    echo "No results found.";\n}\n\n$mysqli->close();\n?>