#!/opt/lampp/bin/php -q\n<?php\nini_set('html_errors', false);\nini_set('implicit_flush', true);\nini_set('max_execution_time', 0);\nini_set('memory_limit', '16M'); // Set a reasonable memory limit\n\nif ($argc > 1) {\n    $input = trim($argv[1]);\n    $parts = explode(' ', $input);\n    $url = $parts[0];\n\n    // Perform the URL rewriting\n    if (strpos($url, 'video.yahoo.com') !== false) {\n        $url = str_replace('video.yahoo.com', 'youtube.com', $url);\n    }\n\n    // Output the rewritten URL\n    echo $url . "\n";\n}\nexit(0);\n?>