<?php\nheader('Content-Type: application/json');\n$region_id = intval($_GET['region_id']); // Ensure to sanitize input\n\n$cache_key = "region_count_" . $region_id;\n$cached_count = $cache->get($cache_key);\n\nif ($cached_count === false) {\n    $query = "EXPLAIN SELECT * FROM your_table WHERE region_id = $region_id";\n    $result = mysqli_query($connection, $query);\n    $row = mysqli_fetch_assoc($result);\n    $approximate_count = $row['rows'];\n\n    // Store the result in cache for 5 minutes\n    $cache->set($cache_key, $approximate_count, 300);\n} else {\n    $approximate_count = $cached_count;\n}\n\necho json_encode(['count' => $approximate_count]);\n?>