function normalizeTime($input) {\n    // Handle input like "an hour and a half"\n    if (preg_match('/(\d+)\s*hours?\s*and\s*(\d+)\s*minutes?/i', $input, $matches)) {\n        $hours = $matches[1];\n        $minutes = $matches[2];\n        return sprintf('%02d:%02d', $hours, $minutes);\n    }\n\n    // Handle input like "27.52" as minutes\n    if (preg_match('/^\d+(\.\d+)?$/', $input)) {\n        $minutes = round($input);\n        $hours = floor($minutes / 60);\n        $remainder_minutes = $minutes % 60;\n        return sprintf('%02d:%02d', $hours, $remainder_minutes);\n    }\n\n    // Handle input like "5:24"\n    if (preg_match('/^(\d{1,2}):(\d{2})$/', $input, $matches)) {\n        $hours = $matches[1];\n        $minutes = $matches[2];\n        return sprintf('%02d:%02d', $hours, $minutes);\n    }\n\n    // If no matching pattern is found, return an error or default value\n    return 'Invalid input';\n}\n\n// Examples\necho normalizeTime('an hour and a half'); // Output: 01:30\necho normalizeTime('27.52');              // Output: 00:28\necho normalizeTime('5:24');               // Output: 05:24