$array = array(\n    array('human' => '28 Feb, 2009', 'db' => '20090228', 'description' => 'Some text here'),\n    array('human' => '19 Mar, 2009', 'db' => '20090319', 'description' => 'Some text here'),\n    array('human' => '5 Apr, 2009', 'db' => '20090405', 'description' => 'Some text here'),\n    array('human' => '19 Apr, 2009', 'db' => '20090419', 'description' => 'Some text here'),\n    array('human' => '2 May, 2009', 'db' => '20090502', 'description' => 'Some text here')\n);\n\n// Custom comparison function\nfunction compare_db_dates($a, $b) {\n    return strcmp($a['db'], $b['db']);\n}\n\n// Sort the array using usort and the custom comparison function\nusort($array, 'compare_db_dates');\n\n// Output the sorted array\nprint_r($array);