<?php\n// First Loop: Display posts from Category 1\n$args1 = array(\n    'category_name' => 'category1', // Replace 'category1' with your category slug\n    'posts_per_page' => 5,           // Number of posts to display\n);\n$query1 = new WP_Query($args1);\n\nif ($query1->have_posts()) : \n    while ($query1->have_posts()) : $query1->the_post(); ?>\n        <h2><?php the_title(); ?></h2>\n        <div><?php the_excerpt(); ?></div>\n    <?php endwhile;\n    wp_reset_postdata(); // Reset post data to avoid conflicts\nendif; \n\n// Second Loop: Display posts from Category 2\n$args2 = array(\n    'category_name' => 'category2', // Replace 'category2' with your category slug\n    'posts_per_page' => 5,           // Number of posts to display\n);\n$query2 = new WP_Query($args2);\n\nif ($query2->have_posts()) : \n    while ($query2->have_posts()) : $query2->the_post(); ?>\n        <h2><?php the_title(); ?></h2>\n        <div><?php the_excerpt(); ?></div>\n    <?php endwhile;\n    wp_reset_postdata(); // Reset post data to avoid conflicts\nendif;\n?>