<?php\n// Connect to the database\n$mysqli = new mysqli("localhost", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Retrieve and sanitize the input\n$site = strtolower(trim($_POST['site']));\n$remove_array = array("/", "\""); // Define any characters you want to remove\n$site = str_replace($remove_array, '', $site);\n\n// Prepare an SQL statement for execution\n$stmt = $mysqli->prepare("INSERT INTO `$find` (column_name1, column_name2, column_name3, column_name4) VALUES (NULL, ?, 1, 0)");\n\n// Bind variables to the prepared statement as parameters\n$stmt->bind_param("s", $site);\n\n// Execute the prepared statement\n$stmt->execute();\n\n// Close the statement and connection\n$stmt->close();\n$mysqli->close();\n?>