<?php\n$dom = new DOMDocument('1.0', 'UTF-8');\n\n// Create the root element\n$root = $dom->createElement('root');\n$dom->appendChild($root);\n\n// Create the mixed content node\n$mixedNode = $dom->createElement('mixedContent');\n\n// Create a text node\n$textNode1 = $dom->createTextNode('This is some ');\n\n// Append the text node to the mixed content node\n$mixedNode->appendChild($textNode1);\n\n// Create a child element with some content\n$childElement = $dom->createElement('child', 'mixed');\n$mixedNode->appendChild($childElement);\n\n// Create another text node\n$textNode2 = $dom->createTextNode(' content.');\n\n// Append the second text node to the mixed content node\n$mixedNode->appendChild($textNode2);\n\n// Append the mixed content node to the root element\n$root->appendChild($mixedNode);\n\n// Save and display the XML\necho $dom->saveXML();\n?>