// Sample array\n$array = array(\n    array('28th February, 2009' => 'bla'),\n    array('19th March, 2009' => 'bla'),\n    array('5th April, 2009' => 'bla'),\n    array('19th April, 2009' => 'bla'),\n    array('2nd May, 2009' => 'bla')\n);\n\n// Custom comparison function\nfunction compare_dates($a, $b) {\n    $date_a = DateTime::createFromFormat('jS F, Y', key($a));\n    $date_b = DateTime::createFromFormat('jS F, Y', key($b));\n    return $date_a <=> $date_b;\n}\n\n// Sort the array using usort and the custom comparison function\nusort($array, 'compare_dates');\n\n// Output the sorted array\nprint_r($array);