class UserDTO {\n         public $id;\n         public $name;\n         public $email;\n     }\n\n     class UserRepository {\n         public function getUserById($id) {\n             $user = DB::table('users')->select('id', 'name', 'email')->where('id', $id)->first();\n             $userDTO = new UserDTO();\n             $userDTO->id = $user->id;\n             $userDTO->name = $user->name;\n             $userDTO->email = $user->email;\n             return $userDTO;\n         }\n     }