function extractTextBetweenParentheses($input) {\n    // Use a regular expression to match content between parentheses\n    preg_match('/\(([^)]+)\)/', $input, $matches);\n    \n    // Return the matched content or an empty string if no match is found\n    return isset($matches[1]) ? $matches[1] : '';\n}\n\n// Test cases\n$input1 = "(writer) &";\n$input2 = "with (additional dialogue)";\n\necho extractTextBetweenParentheses($input1); // Outputs: writer\necho "\n";\necho extractTextBetweenParentheses($input2); // Outputs: additional dialogue