<?php\nfunction findRanges($numbers) {\n    sort($numbers); // Ensure the array is sorted\n    $ranges = [];\n    $start = $numbers[0];\n    $end = $numbers[0];\n\n    for ($i = 1; $i < count($numbers); $i++) {\n        if ($numbers[$i] == $end + 1) {\n            $end = $numbers[$i];\n        } else {\n            if ($start == $end) {\n                $ranges[] = $start;\n            } else {\n                $ranges[] = "$start-$end";\n            }\n            $start = $numbers[$i];\n            $end = $numbers[$i];\n        }\n    }\n\n    if ($start == $end) {\n        $ranges[] = $start;\n    } else {\n        $ranges[] = "$start-$end";\n    }\n\n    return implode(',', $ranges);\n}\n\n$numbers = array(1, 3, 4, 5, 6, 8, 11, 12, 14, 15, 16);\necho findRanges($numbers); // Outputs: 1,3-6,8,11-12,14-16\n?>