<?php\n$filename = 'path/to/your/binaryfile.bin';\n$pattern = "\x12\x34"; // Example 2-byte pattern to search for\n\n// Open the file for reading in binary mode\n$handle = fopen($filename, 'rb');\nif ($handle === false) {\n    die('Failed to open file for reading');\n}\n\nwhile (!feof($handle)) {\n    $byte1 = fread($handle, 1);\n    $byte2 = fread($handle, 1);\n    if ($byte1 === false || $byte2 === false) {\n        break;\n    }\n\n    if ($byte1 . $byte2 === $pattern) {\n        echo "Pattern found!\n";\n    }\n}\n\nfclose($handle);\n?>