<?php\n// Start output buffering\nob_start();\n\n// Your PHP and HTML code here\n?>\n<!DOCTYPE html>\n<html>\n<head>\n    <title>Example Page</title>\n</head>\n<body>\n    <h1>Hello, World!</h1>\n    <p>This is a sample page.</p>\n</body>\n</html>\n<?php\n// Get the contents of the output buffer\n$output = ob_get_clean();\n\n// Define a filter function\nfunction filter_output($output) {\n    // Example filter: convert all text to uppercase\n    return strtoupper($output);\n}\n\n// Pass the output through the filter function\n$filtered_output = filter_output($output);\n\n// Display the filtered output\necho $filtered_output;\n?>