<?php\n// Database connection\n$dsn = 'mysql:host=your_host;dbname=your_dbname';\n$username = 'your_username';\n$password = 'your_password';\n$options = [\n    PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION,\n    PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,\n];\n\ntry {\n    $database = new PDO($dsn, $username, $password, $options);\n} catch (PDOException $e) {\n    die('Connection failed: ' . $e->getMessage());\n}\n\n// Dynamic parameter\n$dynamic_value = 'something else';\n\n// Prepare and execute the query\n$query = "SELECT * FROM widgets WHERE something = :dynamic_value";\n$stmt = $database->prepare($query);\n$stmt->bindParam(':dynamic_value', $dynamic_value, PDO::PARAM_STR);\n$stmt->execute();\n\n// Loop through the results\nforeach ($stmt as $result) {\n    echo $result["widget_name"];\n}\n?>