$ldapconn = ldap_connect("ldap://localhost") or die("Could not connect to LDAP server.");\n   \n   if ($ldapconn) {\n       ldap_set_option($ldapconn, LDAP_OPT_PROTOCOL_VERSION, 3);\n       ldap_bind($ldapconn, "cn=admin,dc=example,dc=com", "admin_password");\n\n       $dn = "cn=John Doe,ou=contacts,dc=example,dc=com";\n       $entry = array();\n       $entry["cn"] = "John Doe";\n       $entry["sn"] = "Doe";\n       $entry["mail"] = "john.doe@example.com";\n       $entry["objectclass"] = array("inetOrgPerson", "top");\n\n       ldap_add($ldapconn, $dn, $entry);\n\n       ldap_close($ldapconn);\n   }