use Drupal\user\Entity\User;\n\n// The role ID you want to filter by.\n$role_id = 'your_role_id';\n\n// Load users by role.\n$user_query = \Drupal::entityQuery('user')\n  ->condition('status', 1) // Optional: Only active users.\n  ->condition('roles', $role_id);\n$uids = $user_query->execute();\n$users = User::loadMultiple($uids);\n\n// Print user names.\nforeach ($users as $user) {\n  // Replace 'name' with any user property you need.\n  echo $user->get('name')->value . "\n";\n}