// Before redirecting to PayPal\n$session_id = session_id();\nlog("Initial session ID: " . $session_id);\n$_SESSION['paypal_session_id'] = $session_id;\n\n// In return_from_paypal.php\nif (isset($_GET['session_id'])) {\n    session_id($_GET['session_id']);\n    session_start();\n    log("Session ID after restoration: " . session_id());\n    \n    if (isset($_SESSION['paypal_session_id'])) {\n        log("Session restored successfully with ID: " . $_SESSION['paypal_session_id']);\n    } else {\n        log("Failed to restore session.");\n    }\n} else {\n    log("No session ID provided.");\n}