<?php\n// Database connection parameters\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n// Connect to the database\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Get the monsterID from the GET request\n$monsterID = $_GET['monsterid'];\n\n// Prepare and bind\n$stmt = $conn->prepare("SELECT id FROM enemies WHERE monsterid = ?");\n$stmt->bind_param("i", $monsterID);\n\n// Execute the statement\n$stmt->execute();\n\n// Store the result\n$stmt->store_result();\n\n// Check if any rows were returned\nif ($stmt->num_rows > 0) {\n    // Monster ID exists, run your PHP code here\n    echo "Monster ID exists.";\n    // {RUN PHP}\n} else {\n    // Monster ID does not exist, run your PHP code here\n    echo "Monster ID does not exist.";\n    // {RUN PHP}\n}\n\n// Close the statement and connection\n$stmt->close();\n$conn->close();\n?>