<?php\n$apiKey = 'YOUR_API_KEY'; // Replace with your API key\n$baseCurrency = 'AUD'; // Australian Dollar\n$targetCurrencies = 'USD,EUR'; // Target currencies\n\n// Fetch exchange rates\n$url = "https://v6.exchangerate-api.com/v6/$apiKey/latest/$baseCurrency";\n$response = file_get_contents($url);\n\nif ($response !== false) {\n    $exchangeRates = json_decode($response, true);\n\n    if ($exchangeRates['result'] === 'success') {\n        $usdRate = $exchangeRates['conversion_rates']['USD'];\n        $eurRate = $exchangeRates['conversion_rates']['EUR'];\n\n        echo "1 AUD = $usdRate USD\n";\n        echo "1 AUD = $eurRate EUR\n";\n        \n        // Example conversion\n        $priceInAUD = 100; // Example price in AUD\n        $priceInUSD = $priceInAUD * $usdRate;\n        $priceInEUR = $priceInAUD * $eurRate;\n\n        echo "Price in AUD: $priceInAUD\n";\n        echo "Price in USD: $priceInUSD\n";\n        echo "Price in EUR: $priceInEUR\n";\n    } else {\n        echo "Error fetching exchange rates: " . $exchangeRates['error-type'];\n    }\n} else {\n    echo "Error fetching data from API.";\n}\n?>