<?php\n$xmldoc = new DOMDocument();\n$xmldoc->load('sample.xml', LIBXML_NOBLANKS);\n\n$atvID = $_POST['activity'];\n$activities = $xmldoc->getElementsByTagName('activity');\n\nforeach($atvID as $id){\n    $id = $id - 1; // Adjust to zero-indexed\n    $activityNode = $activities->item($id);\n    if ($activityNode) {\n        $activityNode->parentNode->removeChild($activityNode);\n    }\n}\n\n$xmldoc->save('sample.xml');\n?>