function getBaseUrl() {\n    // Get protocol (http or https)\n    $protocol = (!empty($_SERVER['HTTPS']) && $_SERVER['HTTPS'] !== 'off' || $_SERVER['SERVER_PORT'] == 443) ? "https://" : "http://";\n    \n    // Get the server name (domain name) and the script name\n    $domainName = $_SERVER['HTTP_HOST'] . dirname($_SERVER['SCRIPT_NAME']);\n    \n    // Ensure the domain name does not end with a slash\n    $domainName = rtrim($domainName, '/');\n    \n    return $protocol . $domainName . '/';\n}\n\n// Usage\n$baseUrl = getBaseUrl();\necho $baseUrl;\n\n// Example of referencing the CSS file\n$cssFile = $baseUrl . 'css/style.css';\necho '<link rel="stylesheet" type="text/css" href="' . $cssFile . '">';