<?php\nfunction concatenateRTF($rtfArray) {\n    $mergedRTF = '';\n    \n    foreach ($rtfArray as $index => $rtfContent) {\n        if ($index > 0) {\n            // Remove the closing RTF markup and the opening RTF markup\n            $rtfContent = preg_replace('/}\s*\\\\page.*?\\\\par\s*{\\\\rtf1/s', '', $rtfContent);\n        }\n        $mergedRTF .= $rtfContent;\n    }\n\n    return $mergedRTF;\n}\n\n// Example usage\n$rtfDocuments = [\n    '{\rtf1\ansi\ansicpg1252\deff0\deflang1033{\fonttbl{\f0\fswiss\fcharset0 Arial;}}\viewkind4\uc1\pard\f0\fs20 First Document\par}\page',\n    '{\rtf1\ansi\ansicpg1252\deff0\deflang1033{\fonttbl{\f0\fswiss\fcharset0 Arial;}}\viewkind4\uc1\pard\f0\fs20 Second Document\par}\page',\n    '{\rtf1\ansi\ansicpg1252\deff0\deflang1033{\fonttbl{\f0\fswiss\fcharset0 Arial;}}\viewkind4\uc1\pard\f0\fs20 Third Document\par}\page'\n];\n\n$mergedRTF = concatenateRTF($rtfDocuments);\necho $mergedRTF;\n?>