<?php\nfunction getBrowser() {\n    $userAgent = $_SERVER['HTTP_USER_AGENT'];\n    $browser = "Unknown Browser";\n\n    $browserArray = array(\n        '/msie/i' => 'Internet Explorer',\n        '/firefox/i' => 'Firefox',\n        '/safari/i' => 'Safari',\n        '/chrome/i' => 'Chrome',\n        '/edge/i' => 'Edge',\n        '/opera/i' => 'Opera',\n        '/mobile/i' => 'Mobile Browser',\n        '/sony/i' => 'SonyEricsson',\n        '/samsung/i' => 'Samsung',\n        '/nokia/i' => 'Nokia'\n    );\n\n    foreach ($browserArray as $regex => $value) {\n        if (preg_match($regex, $userAgent)) {\n            $browser = $value;\n        }\n    }\n    return $browser;\n}\n\necho "You are using " . getBrowser();\n?>