function ExecuteQuery($SQL) {\n    $conn = oci_connect('username', 'password', 'hostname/dbname');\n    if (!$conn) {\n        $e = oci_error();\n        throw new OCI_Exception($e['message']);\n    }\n\n    $stid = oci_parse($conn, $SQL);\n    if (!$stid) {\n        $e = oci_error($conn);\n        throw new OCI_Exception($e['message']);\n    }\n\n    $r = oci_execute($stid);\n    if (!$r) {\n        $e = oci_error($stid);\n        throw new OCI_Exception($e['message']);\n    }\n\n    oci_free_statement($stid);\n    oci_close($conn);\n}