<?php\n// Sample input string\n$input = '<option value="TTO">1031</option><option value="187">187</option><option value="TWO">2SK8</option><option value="411">411</option><option value="AEL">Abec 11</option><option value="ABE">Abec11</option><option value="ACE">Ace</option><option value="ADD">Addikt</option><option value="AFF">Affiliate</option><option value="ALI">Alien Workshop</option><option value="ALG">Alligator</option><option value="ALM">Almost</option>';\n\n$pattern = '/<option value="([^"]+)">([^<]+)<\/option>/';\npreg_match_all($pattern, $input, $matches, PREG_SET_ORDER);\n\n$options = [];\nforeach ($matches as $match) {\n    $options[$match[1]] = $match[2];\n}\n\n// Output the result for verification\nprint_r($options);\n?>