<?php\n$file = 'ff.log';\n\n// Run the tail command in follow mode and pipe it to awk to always print the last line\n$command = "tail -f $file | awk '{ last = $0 } END { print last }'";\n\n// Open a process to execute the command\n$process = popen($command, 'r');\n\n// Read the output from the process\n$lastLine = '';\nif (is_resource($process)) {\n    while ($line = fgets($process)) {\n        $lastLine = $line;\n        echo "--$lastLine--\n";\n        // You can add a sleep here if you want to control the output frequency\n        // sleep(1);\n    }\n    pclose($process);\n}\n?>