$sender_id = 1; // example value\n$receiver_id = 2; // example value\n\n// Create a new mysqli object with database connection parameters\n$mysqli = new mysqli("hostname", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Prepare the SQL statement\n$stmt = $mysqli->prepare("SELECT usrID, usrFirstName, usrLastName FROM tblusers WHERE usrID IN (?, ?)");\n\n// Bind the variables to the prepared statement as integers\n$stmt->bind_param("ii", $sender_id, $receiver_id);\n\n// Execute the query\n$stmt->execute();\n\n// Get the result\n$result = $stmt->get_result();\n\n// Fetch the data and process it\nwhile ($row = $result->fetch_assoc()) {\n    echo "ID: " . $row['usrID'] . "<br>";\n    echo "First Name: " . $row['usrFirstName'] . "<br>";\n    echo "Last Name: " . $row['usrLastName'] . "<br><br>";\n}\n\n// Close the statement and connection\n$stmt->close();\n$mysqli->close();