<?php\nregister_shutdown_function('shutdown');\n\nfunction shutdown() {\n    $error = error_get_last();\n    if ($error !== NULL) {\n        $info = "[SHUTDOWN] file: {$error['file']} | line: {$error['line']} | message: {$error['message']}";\n        error_log($info);\n        echo $info;\n    }\n}\n\necho "Running script...\n";\n\n// Your script logic here\n\nexit();  // Example exit point\n?>