class MyClass {\n    public static $staticProperty = 'static value';\n    public $instanceProperty = 'instance value';\n\n    public static function staticMethod() {\n        echo self::$staticProperty; // Correct usage for static context\n    }\n\n    public function instanceMethod() {\n        echo $this->instanceProperty; // Correct usage for instance context\n    }\n}\n\n$obj = new MyClass();\n$obj->instanceMethod(); // Outputs: instance value\nMyClass::staticMethod(); // Outputs: static value