<?php\nfunction convertPoToMo($poFilePath, $moFilePath) {\n    // Check if the .po file exists\n    if (!file_exists($poFilePath)) {\n        throw new Exception("The .po file does not exist: $poFilePath");\n    }\n\n    // Use the msgfmt command to convert .po to .mo\n    $command = sprintf('msgfmt -o %s %s', escapeshellarg($moFilePath), escapeshellarg($poFilePath));\n    $output = [];\n    $returnVar = 0;\n\n    // Execute the command\n    exec($command, $output, $returnVar);\n\n    // Check if the command was successful\n    if ($returnVar !== 0) {\n        throw new Exception("Error converting .po to .mo file: " . implode("\n", $output));\n    }\n\n    return true;\n}\n\n// Example usage\ntry {\n    convertPoToMo('/path/to/yourfile.po', '/path/to/yourfile.mo');\n    echo "Conversion successful!";\n} catch (Exception $e) {\n    echo "Error: " . $e->getMessage();\n}\n?>