$friends = [/* array of 2,000 friendID numbers */];\n$bulletins = [\n    // array of 10,000 bulletin entries, each entry is an associative array with 'bulletinID' and 'userID'\n    ['bulletinID' => 1, 'userID' => 123],\n    ['bulletinID' => 2, 'userID' => 456],\n    // ...\n];\n\n$friendIDs = array_flip($friends); // Flip to use friend IDs as keys for faster lookup\n$matchingBulletins = array_filter($bulletins, function($bulletin) use ($friendIDs) {\n    return isset($friendIDs[$bulletin['userID']]);\n});\n\n$matchingBulletinIDs = array_column($matchingBulletins, 'bulletinID');\n\n// Limit to the first 50 bulletin IDs\n$limitedBulletinIDs = array_slice($matchingBulletinIDs, 0, 50);\n\n// Output the result\nprint_r($limitedBulletinIDs);