<?php\nfunction isJpegCorrupted($filePath) {\n    if (!file_exists($filePath)) {\n        return true; // File does not exist\n    }\n\n    // Open the file\n    $file = fopen($filePath, "rb");\n    if (!$file) {\n        return true; // Unable to open the file\n    }\n\n    // Read the first few bytes\n    $header = fread($file, 2);\n    fclose($file);\n\n    // Check for JPEG SOI marker (Start Of Image, 0xFFD8)\n    if ($header !== "\xFF\xD8") {\n        return true; // Not a valid JPEG file\n    }\n\n    // Use finfo to check MIME type\n    $finfo = finfo_open(FILEINFO_MIME_TYPE);\n    $mimeType = finfo_file($finfo, $filePath);\n    finfo_close($finfo);\n\n    return $mimeType !== 'image/jpeg';\n}\n\n// Usage example\n$filePath = 'path/to/your/image.jpg';\nif (isJpegCorrupted($filePath)) {\n    echo "The JPEG file is corrupted or not a valid JPEG.";\n} else {\n    echo "The JPEG file is valid.";\n}\n?>