<?php\nfunction getDayOfQuarter($date) {\n    // Get the month and day of the given date\n    $month = (int)date('n', strtotime($date));\n    $day = (int)date('j', strtotime($date));\n    \n    // Determine the start month of the current quarter\n    $quarterStartMonth = (int)(($month - 1) / 3) * 3 + 1;\n    \n    // Get the start date of the current quarter\n    $quarterStartDate = date('Y-m-d', strtotime(date('Y', strtotime($date)) . '-' . $quarterStartMonth . '-01'));\n    \n    // Calculate the difference in days between the given date and the start of the quarter\n    $dayOfQuarter = (strtotime($date) - strtotime($quarterStartDate)) / (60 * 60 * 24) + 1;\n    \n    return $dayOfQuarter;\n}\n\n// Example usage\n$date = '2023-08-05';\necho 'Day of the current quarter: ' . getDayOfQuarter($date);\n?>